    private void buttonConcluir2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonConcluir2ActionPerformed
        
        String nomeLivro = edititulo2.getText();
        String novaEditora = editEditora.getText();
        String novoGenero = editGenero.getText();
        String novaPrateleira = editPrateleira.getText();
        int novaQuantidade = Integer.parseInt(editQuantidade2.getText());
        int novoCorredor = Integer.parseInt(editCorredor.getText());
        
        Disponivel novo = new Disponivel();
        novo.setnomeLivro(nomeLivro);
        novo.setEditora(novaEditora);
        novo.setGenero(novoGenero);
        novo.setPrateleira(novaPrateleira);
        novo.setQuantidade(novaQuantidade);
        novo.setCorredor(novoCorredor);
      dd.atualizar(novo);


        JOptionPane.showMessageDialog(this, "Atualizado com Sucesso");

    }//GEN-LAST:event_buttonConcluir2ActionPerformed

