    private void buttonConcluir1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonConcluir1ActionPerformed
    
        
       Disponivel disp = dd.buscarPorNome(titulo1.getText());
        if (disp != null) {
            editora1.setText(disp.getEditora());
            genero1.setText(disp.getGenero());
            prateleira1.setText(disp.getPrateleira());
            quantidade1.setText(String.valueOf(disp.getQuantidade()));
            corredor1.setText(String.valueOf(disp.getCorredor()));
            JOptionPane.showMessageDialog(this, "Livro Encontrado !");
        } else {
            JOptionPane.showMessageDialog(this, "Livro n√£o encontrado."); 
        }

    }//GEN-LAST:event_buttonConcluir1ActionPerformed

    private void excluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_excluirActionPerformed

        dd.remover(titulo1.getText());
  
        JOptionPane.showMessageDialog(this, "Livro apagado !");

        titulo1.setText("");
        editora1.setText("");
        genero1.setText("");
        prateleira1.setText("");
        quantidade1.setText("");
        corredor1.setText("");

    }//GEN-LAST:event_excluirActionPerformed

